<?phpinclude "./config.php";// update ARP table on each request /!\get_arp_table();// client mac accepted ?, si il n'a pas le droit il est renvoyé un json d'erreurperipheral_can_fetch_data();!isset($_REQUEST['c']) ? $_controler = 'home' : $_controler = $_REQUEST['c'];switch ( $_controler ) {    case 'command' :        include URI_CONTROL . "/c_command.php";        break;    case 'peripheral' :        include URI_CONTROL . "/c_peripheral.php";        break;    case 'robot' :        include URI_CONTROL . "/c_robot.php";        break;    case 'user' :        include URI_CONTROL . "/c_user.php";        break;    case 'admin' :        include URI_CONTROL . "/c_admin.php";        break;    default :        include URI_CONTROL . "c_home.php";        break;}